import styled from 'styled-components';

export const Input = styled.input` {
  width: 100%;
  padding: 12px 20px;
  margin: 8px 0;
  display: inline-block;
  border: 1px solid #ccc;
  border-radius: 4px;
  box-sizing: border-box;
  border-color: ${(props) => (props.error ? 'red' : '')};
}`;

export const Error = styled.lable`{
  font-size:10px;
  color:red
}`;
